mapper.select:
	   select * from t_sys_staff where

            staff_group = #{staffGroup} and

	    name like #{name} ORDER BY id desc LIMIT #{pageNo}, 10;

	   SELECT COUNT(*) FROM t_sys_staff where

           staff_group = #{staffGroup} and

        name like #{name};

mapper.insert:
insert into t_sys_staff (
name,
staff_group,
password
) VALUES
(
#{name},
#{staffGroup},
#{password}
)

mapper.update:
UPDATE t_sys_staff SET staff_group = #{staffGroup} ,
password = #{password}
where id = #{id}
